function headerSmoothHide(){let e=window.pageYOffset;window.onscroll=function(){let t=window.pageYOffset;document.getElementById("js-navigation-bar").style.top=e>t?"0":"-110px",e=t}}function setMenuScrollNavigation(){document.querySelectorAll(".js-menu-item").forEach(e=>{e.addEventListener("click",t=>{t.preventDefault();const o=e.getAttribute("href");document.querySelector(o).scrollIntoView({behavior:"smooth",block:"start"})})})}function accordionList(){const e=document.querySelectorAll(".faq__accordion");document.querySelectorAll(".faq__button");e.forEach(e=>{e.addEventListener("click",t=>{e.querySelector(".faq__accordion_hide").classList.toggle("hidden");e.querySelector(".faq__arrow").classList.toggle("rotate")})})}window.addEventListener("load",e=>{headerSmoothHide(),accordionList(),setMenuScrollNavigation()});